import { Component } from '@angular/core';

@Component({
  selector: 'app-input-mult-files',
  templateUrl: './input-mult-files.component.html',
  styleUrls: ['./input-mult-files.component.scss']
})
export class InputMultFilesComponent {
  xmlInputChange(fileInputEvent: any) {
    console.log(fileInputEvent.target.files);
  }
}
<span>Selecione um ou mais arquivos XML para a mineração de dados.</span>
<div class="input-container">
  <input
    #xmlInput
    type="file"
    hidden="true"
    onclick="this.value=null"
    (change)="xmlInputChange($event)"
    accept=".xml"
    multiple="multiple"
  />
  <button
    mat-raised-button
    color="primary"
    (click)="xmlInput.click()"
  >
    Selecionar Arquivos
  </button>
</div>
