import { Component } from '@angular/core';

@Component({
  selector: 'app-input-mult-files',
  templateUrl: './input-mult-files.component.html',
  styleUrls: ['./input-mult-files.component.scss'],
})
export class InputMultFilesComponent {
  files: Array<XmlFile> = [];

  xmlInputChangeHandler(fileInputEvent: any) {
    this.files = fileInputEvent.target.files;
    console.log(this.files);
  }

  fileRemoveClickHandler(file: Object) {
    console.log(file);
  }
}

export interface XmlFile {
  name: string;
  size: bigint;
  type: string;
  file: any;
}
